(function() {
  define(function() {

    /*
     * Use invariant() to assert state which your program assumes to be true.
     *
     * Provided arguments are automatically type checked and logged correctly to the console
     * Chrome's console.log sprintf format.
     *
     * ex: invariant(!hasFired, "hasFired was expected to be true but was", hasFired)
     *
     * The invariant message will be stripped in production, but the invariant
     * will remain to ensure logic does not differ in production.
     */
    return function(condition, message) {
      var error;
      if (!condition) {
        if (!args.length) {
          error = new Error("Minified exception occurred; use the non-minified dev environment\nfor the full error message and additional helpful warnings.");
        } else {
          error = new Error(message);
        }
        error.framesToPop = 1;
        throw error;
      }
    };
  });

}).call(this);
